        private void listBox1_SelectedIndexChanged(object sender, EventArgs e)
        {
            string[] temp = listBox1.SelectedItem.ToString().Split('/');
            foreach (product p in pr) 
              if(temp[1] == p.id_product) {
                  id_p.Text = p.id_product;
                  name_p.Text = p.name;
                  active_p.Checked = true;
                  price_p.Text = p.price;
                  w_price_p.Text = p.wholesale_price;
                  category_p.Text = p.id_category_default;
                  desc_p.Text = p.description;
                  short_desc_p.Text = p.description_short;
                  meta_title_p.Text = p.meta_title;
                  meta_keyword_p.Text = p.meta_keywords;
                  meta_desc_p.Text = p.meta_description;
                  wieght_p.Text = p.weight;
                  width_p.Text = p.width;
                  quality_p.Text = p.quantity;
                  manufacture_p.Text = p.id_manufacturer;
                  suppler_p.Text = p.id_supplier;
                  ean13_p.Text = p.ean13;
                  long_p.Text ="10";
                  foreach (image i in img)  
                  if(p.id_product == i.id_product.ToString()) {
                    //  MessageBox.Show(AppDomain.CurrentDomain.BaseDirectory + createpatchimg(i.id_image) + i.id_image + ".jpg");
                      img_p.Load(i.patch + @"\" + i.id_image + ".jpg");
                      break;
                  }
                  break;
            }
        }

        string createpatchimg(int index)
        {
            string patch = "";
            StringBuilder num = new StringBuilder(Convert.ToString(index));
            for (int i = 0; i < num.Length; i++)
                patch += @"\" + num[i];
            return patch;
        }



        private void сохранитьToolStripMenuItem_Click(object sender, EventArgs e)
        {
            string[] temp = listBox1.SelectedItem.ToString().Split('/');
            foreach (product p in pr)
                if (temp[1] == p.id_product)
                {
                    p.id_product = id_p.Text;
                    p.name = name_p.Text;
                    p.price = price_p.Text;
                    p.wholesale_price = w_price_p.Text;
                    p.id_category_default = category_p.Text;
                    p.description = desc_p.Text;
                    p.description_short = short_desc_p.Text;
                    p.meta_title = meta_title_p.Text;
                    p.meta_keywords = meta_keyword_p.Text;
                    p.meta_description = meta_desc_p.Text;
                    p.weight = wieght_p.Text;
                    p.width = width_p.Text;
                    p.quantity = quality_p.Text ;
                    p.id_manufacturer = manufacture_p.Text;
                    p.id_supplier = suppler_p.Text;
                    p.ean13 = ean13_p.Text;
                    MessageBox.Show("Запись сохрнанена", "Оповещение", MessageBoxButtons.OK);
                    break;
                }
        }        
